import React from "react";

function CreateArea(props) {

  const [note, setTitle] = React.useState("");

  function getNote (event) {
    const note = {
      title: event.target.value,
      conetnt: event.target.value
    }
    setTitle(note.title);
    setContent(note.content);
  }


  return (
    <div>
      <form>
        <input onChange={getNote} name="title" placeholder="Title" value={title}/>
        <textarea onChange={getContent} name="content" value={note.content} placeholder="Take a note..." rows="3" />
        <button onClick={
          (e) => 
          {
            props.onAdd(note); 
            setContent("");
            setTitle("");
            e.preventDefault();
          }
        } 
        >
        Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
